import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  Box,
  IconButton,
  CircularProgress,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { generateRecipeWithAI } from '../api/recipe';
import Autocomplete from '@mui/material/Autocomplete';

const unitOptions = ['g', '個', '本', 'ml', 'カップ', '枚', '小さじ', '大さじ', '適量'];

const ingredientOptions = [
  'たまねぎ', 'にんじん', 'じゃがいも', '鶏もも肉', '豚肉', '牛肉', 'ピーマン', 'トマト', 'キャベツ', 'レタス',
  'きゅうり', '卵', '牛乳', 'バター', '小麦粉', '砂糖', '塩', 'しょうゆ', 'みそ', '酢', 'ごま油', 'オリーブオイル',
  // ...他にもよく使う材料を追加
];

function RecipeGenerator() {
  const navigate = useNavigate();
  const [ingredients, setIngredients] = useState([{ name: '', amount: '', unit: '' }]);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [servings, setServings] = useState(2);

  const handleIngredientChange = (index, field, value) => {
    const newIngredients = [...ingredients];
    newIngredients[index] = {
      ...newIngredients[index],
      [field]: value,
    };
    setIngredients(newIngredients);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, { name: '', amount: '', unit: '' }]);
  };

  const removeIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleGenerate = async () => {
    setLoading(true);
    setError('');
    setResult(null);
    try {
      const names = ingredients.map(ing => ing.name).filter(Boolean);
      const res = await generateRecipeWithAI(names, servings);
      navigate(`/recipes/${res.recipe.id}`);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          材料からレシピを生成
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h6" gutterBottom>
              手持ちの材料を入力してください
            </Typography>
            {ingredients.map((ingredient, index) => (
              <Box key={index} sx={{ display: 'flex', gap: 2, mb: 2 }}>
                <TextField
                  label="材料名"
                  value={ingredient.name}
                  onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
                  required
                />
                <TextField
                  label="量"
                  value={ingredient.amount}
                  onChange={(e) => handleIngredientChange(index, 'amount', e.target.value)}
                  required
                />
                <FormControl required sx={{ minWidth: 100 }}>
                  <InputLabel>単位</InputLabel>
                  <Select
                    value={ingredient.unit}
                    label="単位"
                    onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                  >
                    {unitOptions.map((option) => (
                      <MenuItem key={option} value={option}>{option}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <IconButton
                  color="error"
                  onClick={() => removeIngredient(index)}
                  disabled={ingredients.length === 1}
                >
                  <DeleteIcon />
                </IconButton>
              </Box>
            ))}
            <Button
              startIcon={<AddIcon />}
              onClick={addIngredient}
              variant="outlined"
              sx={{ mt: 1 }}
            >
              材料を追加
            </Button>
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="何人前"
              type="number"
              value={servings}
              onChange={(e) => setServings(Number(e.target.value))}
              required
              sx={{ mb: 1 }}
            />
          </Grid>

          <Grid item xs={12}>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/')}
              >
                キャンセル
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={handleGenerate}
                disabled={loading || ingredients.some(ing => !ing.name || !ing.amount || !ing.unit)}
              >
                {loading ? <CircularProgress size={24} /> : 'レシピを生成'}
              </Button>
            </Box>
          </Grid>
        </Grid>
        {error && <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>}
        {result && (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h5">{result.recipe.title}</Typography>
            <Typography>{result.recipe.description}</Typography>
            <pre>{result.recipe.instructions}</pre>
            <Typography variant="h6" sx={{ mt: 2 }}>バリエーション</Typography>
            <ul>
              {result.variations.map((v, i) => (
                <li key={i}>
                  <strong>{v.title}</strong>: {v.description}
                  <pre>{Array.isArray(v.instructions) ? v.instructions.join('\n') : v.instructions}</pre>
                </li>
              ))}
            </ul>
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default RecipeGenerator;
